mapper.update:
UPDATE TBL_BOARD
   SET BRDTITLE=#{brdtitle}
     , BRDWRITER=#{brdwriter}
     , BRDMEMO=#{brdmemo} 
 WHERE BRDDELETEFLAG='N'
   AND BRDNO=#{brdno}

UPDATE TBL_BOARD
   SET BRDHIT = BRDHIT + 1 
 WHERE BRDNO=#{brdno}

mapper.delete:
UPDATE TBL_BOARD
   SET BRDDELETEFLAG='Y' 
 WHERE BRDNO=#{brdno}


       DELETE 
         FROM TBL_BOARDFILE
        WHERE FILENO IN (
             
                    ${item}
               
       )

mapper.insert:
    
        SELECT IFNULL(MAX(RENO),0)+1 FROM TBL_BOARDREPLY
    

    INSERT INTO TBL_BOARDREPLY(BRDNO, RENO, REWRITER, REDELETEFLAG, REMEMO, REDATE, REORDER, REPARENT, REDEPTH)
    VALUES (#{brdno}, #{reno}, #{rewriter}, 'N', #{rememo}, NOW(), #{reorder},
               
                   #{reno}, 0
                   #{reparent}, #{redepth}
               
           )

mapper.select:
SELECT BRDNO, REDEPTH+1 REDEPTH, REORDER 
  FROM TBL_BOARDREPLY
 WHERE RENO=#{reparent}

mapper.update:

       UPDATE TBL_BOARDREPLY
          SET REORDER = REORDER + 1 
        WHERE BRDNO=#{brdno} AND REORDER>#{reorder}

mapper.select:
SELECT IFNULL(MAX(REORDER),0)+1
  FROM TBL_BOARDREPLY
 WHERE BRDNO=#{brdno}

mapper.update:

       UPDATE TBL_BOARDREPLY
          SET REDELETEFLAG='Y' 
        WHERE RENO=#{reno}

UPDATE TBL_BOARDREPLY
   SET REMEMO=#{rememo} 
 WHERE RENO=#{reno}
